function() {

  var cookie = {{CK - __utmzz}};

  if (!cookie) return;

  var parts = cookie.split('|');
  var dict = {
  	utmcsr: 'source',
    utmcmd: 'medium',
    utmccn: 'campaign',
    utmcct: 'content',
    utmctr: 'keyword'
  };
  var output = {};
  var part;
  var key;
  var val;
  var i;

  for (i = 0; i < parts.length; i++) {

    part = parts[i].split('=');
    key = part[0];
    val = part[1];

    output[dict[key]] = val;

  }

  return output;

}

function() {

  if (!{{Referrer}}) return;

  var a = document.createElement('a');
  a.href = {{Referrer}};

  return a.hostname.match(/[^.]*\.[^.]{2,3}(?:\.[^.]{2,3})?$/)[0];

}

