function() {
  var status = {{Video Status}};
  switch (status) {
    case 'start':
      return 'Play';
    case 'pause':
      return 'Pause';
    case 'seek':
      return 'Seeking';
    case 'progress':
      return + {{Video Percent}} + '% Watched';
    case 'complete':
      return '100% Watched';
  }
}

