function() {
  // 1. Define the variables using your existing DLVs
  var newUrl = {{DLV - gtm.newUrl}};
  var oldUrl = {{DLV - gtm.oldUrl}};

  // 2. Define the cleanup function internally
  // Uses an anchor element (<a>) for robust ES5 URL parsing
  function getPath(url) {
    // Safety check: ensure the value is a string and exists
    if (typeof url !== 'string' || !url) {
      return '';
    }

    // Create a temporary anchor element
    var a = document.createElement('a');
    a.href = url;

    // .pathname contains the path, which excludes the protocol, host, and query string.
    // Ensure leading slash is present.
    var path = a.pathname;

    // Anchor element parsing sometimes fails to include the leading slash,
    // especially for relative URLs, so we ensure it's there.
    if (path.indexOf('/') !== 0) {
        path = '/' + path;
    }

    // Strip query parameters and hash fragments that might remain if the URL 
    // was not fully qualified or if the browser's parsing is inconsistent.
    path = path.split('?')[0];
    path = path.split('#')[0];

    return path;
  }

  // 3. Clean and compare both URLs
  var newPath = getPath(newUrl);
  var oldPath = getPath(oldUrl);

  // Return true if the cleaned paths are NOT equal (i.e., a new page was navigated to)
  return newPath !== oldPath;
}

